fun advent6Part1() {
    println(advent6(input6, 4))
}

fun advent6Part2() {
    println(advent6(input6, 14))
}

fun advent6(input: String, markerSize: Int) =
    markerSize + input.toCharArray().toList().windowed(markerSize).indexOfFirst { chars ->
        chars.toSet().size == markerSize
    }

const val input6 = ""
